mapper.select:
SELECT count(*)
  FROM post
 WHERE bd_no = #{bd_no}

mapper.insert:
INSERT INTO post
	VALUES(pt_no_sequence.nextval
				, #{bd_no}
				, #{id}
				, null
				, pt_no_sequence.nextval
				, #{pt_content}
				, #{pt_title}
				, sysdate
				, 0)

mapper.update:
UPDATE post SET pt_remove = 1
						, pt_title = '삭제된 게시물입니다'
			  WHERE pt_no = #{pt_no}

UPDATE post SET pt_title = #{pt_title}
						 , pt_content = #{pt_content}
			  WHERE pt_no = #{pt_no}
